package in.co.capgemini.calculate;

public class Maths {

	public int add(int i, int j) {
		// TODO Auto-generated method stub
		return i+j;
	}

	public int sub(int i, int j) {
		// TODO Auto-generated method stub
		return i-j;
	}

	public int multiply(int i, int j) {
		// TODO Auto-generated method stub
		return i*j;
	}

	public float divide(int i, int j) {
		// TODO Auto-generated method stub
		return (float)(i/j);
	}

}
